import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; 
import './register.css';// Import useNavigate

function Register() {
  const [formData, setFormData] = useState({
    name: '',
    country: '',
    email: '',
    password: ''
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate(); // Get the navigate function

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = {};
    // Perform validation
    if (formData.name.length > 15) {
      validationErrors.name = 'Name must be 15 characters or less';
    }
    if (formData.country.length > 15) {
      validationErrors.country = 'Country must be 15 characters or less';
    }
    if (!formData.email.match(/^[\w-]+(\.[\w-]+)*@([\w-]+\.)+[a-zA-Z]{2,7}$/)) {
      validationErrors.email = 'Invalid email format';
    }
    if (!formData.password.match(/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])(?=.*[!@#$%^&*]).{8,50}$/)) {
      validationErrors.password = 'Password must contain at least one uppercase letter, one lowercase letter, one number, one special character, and be between 8 to 50 characters long';
    }

    // Set errors state
    setErrors(validationErrors);

    // If there are no validation errors, submit the form
    if (Object.keys(validationErrors).length === 0) {
      try {
        const response = await axios.post('http://localhost:4000/register', formData);
        console.log(response.data);
        // Redirect to login page after successful registration
        navigate('/login'); // Use navigate function to redirect
      } catch (error) {
        console.error('Error registering user:', error);
      }
    }
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Name" onChange={handleChange} />
        {errors.name && <p>{errors.name}</p>}
        <input type="text" name="country" placeholder="Country" onChange={handleChange} />
        {errors.country && <p>{errors.country}</p>}
        <input type="email" name="email" placeholder="Email" onChange={handleChange} />
        {errors.email && <p>{errors.email}</p>}
        <input type="password" name="password" placeholder="Password" onChange={handleChange} />
        {errors.password && <p>{errors.password}</p>}
        <button type="submit">Register</button>
      </form>
      <p>Already have an account? <a href="/login">Login</a></p>
    </div>
  );
}

export default Register;
